import React, {Component} from 'react'
import Movie from '../components/Movie'
import { Grid, Row, Col} from 'react-bootstrap';
import '../css/main.css';
import Search from '../components/Search'
import tmdbLogo from'../img/tmdb.png'
import NavDrawer from '../components/NavDrawer'
import {Link} from 'react-router'
import logo from '../img/kino3.png'
import '../css/search.css';
import {Textfit} from 'react-textfit'
import axios from 'axios'

class Home extends Component{

  constructor(props) {
    super(props)
    this.state = {
      movie: [],
      query: '',
      loading: true
    }
  }

  componentDidMount() {
    this.fetchData()
  }

  componentDidUpdate(prevProps, prevState, snapshot){
    if (this.state.movie !== prevState.movie) {
        this.setState({movie: []})
      this.getInfo()
    }
  }

  fetchData(){
    fetch('https://api.themoviedb.org/3/movie/popular?api_key=17c21a1abd8ae7be6ee8986389011906')
      .then(response=> response.json())
      .then( ({results: movie}) => this.setState({movie}))
  }

  getInfo = () => {
    axios.get('https://api.themoviedb.org/3/search/movie?api_key=17c21a1abd8ae7be6ee8986389011906&total_results=1&query='+ this.state.query)
      .then(({data}) => {
        this.setState({
          movie: data.results,
          loading: false
        })
      })
  }

  handleInputChange = () => {
    this.setState({
      query: this.search.value
    }, () => {
      if (this.state.query && this.state.query.length > 1) {
        if (this.state.query.length % 2 === 0) {
          this.getInfo()
        }
      }
    })
  }

  onKeyDown = () => {
    this.setState({
      query: this.search.value
    }, () => {
      if (this.state.query && this.state.query.length > 1) {
        if (this.state.query.length % 2 === 0) {
          this.getInfo()
        }
      }
      else if (this.state.query.length == 0){
          this.setState({
            searchData: ''
          })
      }
      else {
        this.setState({
          query: '',
        })
      }
    })
  }

  reload = function() {
      if(!window.location.hash) {
          window.location.reload();
      }
  }

  render() {
    var map = Array.from(Array(this.state.movie.length).keys())
    return(
      <div>
        <div className="main-page">
            <Link to= {{pathname: `/`}}>
                <img className="logo-img-full" src={logo}/>
            </Link>
            <NavDrawer/>
            <Grid>
                <Row>
                    <Col xs={12}>
                        <h1 className="pageTitle">Popular</h1>    
                    </Col>
                    <Col xs={12}>
                    <div className="search">
                        <form>
                            <input
                                className="search-box"
                                placeholder="Search movies..."
                                ref={input => this.search = input}
                                onChange={this.handleInputChange}
                            />
                        </form>
                    </div>
                    </Col>
                </Row>
                <Row className="movie-row-container">
                    {map.map(i=>{
                        return <Col className="movie-container" key={map.id} md={2} sm={3} xs={6}>
                                        <Movie id={this.state.movie[i].id}></Movie>
                               </Col>
                    })}
                </Row>
            </Grid>
            <div>
                <footer id="footer">
                    <p className="footer-text"> Repo: <a href="https://github.com/sikidamjanovic/Kino">
                    kino@github.com</a>.</p>
                    <p>&copy; 2018 Sinisa Damjanovic</p>
                    <img src={tmdbLogo} id="tmdbLogo" alt="TMDBLogo"/>
                </footer>
            </div>
        </div>
      </div>
    )
  }
}

export default Home
