import React, { Component } from 'react';
import { usePalette } from 'react-palette'
import '../css/movie-card.css'

class MovieCard extends Component {

    state = {
        data: [],
        loaded: false
    }

    componentDidMount(){
        this.fetchData()
    }

    fetchData(){
        fetch('https://api.themoviedb.org/3/movie/' + this.props.id + '?api_key=17c21a1abd8ae7be6ee8986389011906')
        .then(response => {
            if (!response.ok) {
                throw Error("Network request failed")
            }
            return response
        })
        .then(d => d.json())
        .then(d => {
            this.setState({
                data: d,
                loaded: true
            })
        })
    }

    renderPoster(){
        var loaded = this.state.loaded
        if(loaded){
            var poster = this.state.data.poster_path
            if(poster){
                return <img 
                            src={"https://image.tmdb.org/t/p/w500" + poster} 
                            alt="poster" 
                            id="poster"
                        />
            }else{
                return <p>No Image, Sorry.</p>
            }
        }else{
            return <p>No Image, Sorry.</p>
        }
    }

    renderLine(){
        var path = this.state.data.poster_path
        const { data, loading, error } = usePalette("https://image.tmdb.org/t/p/w500" + path)
        return(
            <div style={{ color: data.vibrant, 
                          width: '2px',
                          height: '100%' 
                        }}></div>
        )
    }

    renderInfo(){
        var loaded = this.state.loaded
        var d = this.state.data
        var path = this.state.data.poster_path

        if(loaded){
            const { data, loading, error } = usePalette("https://image.tmdb.org/t/p/w500" + path)
            return(
                <div id="info-container">
                    <div id="rating-container">
                        <div style={{ color: data.vibrant, width: '2px', height: '100%' }}></div>
                    </div>
                    <div id="title-container">
                        <p id="title">
                            {d.title}
                            <span id="year"> {d.release_date.substring(0, 4)}</span>
                        </p>
                    </div>
                </div>
            )
        }
    }

    render() {
        return (
            <div id="movie-card">
                <div>
                    {this.renderPoster()}
                    {this.renderInfo()}
                </div>
            </div>
        );
    }
}

export default MovieCard;
