import React, {Component} from 'react'
import Movie from '../components/Movie'
import Footer from '../components/Footer'
import { Container, Row, Col, CardColumns, Card} from 'react-bootstrap';
import '../css/main.css';
import {Link} from 'react-router'
import logo from '../img/kino3.png'
import FadeIn from 'react-fade-in';
import '../css/search.css';
var Loader = require('react-loader');

class Home extends Component{

    constructor(props) {
        super(props)
        this.state = {
            movie: [],
            query: 'star wars',
            loaded: false,
            error: false
        }
    }
    reset() {
        this.setState({movie:[]});
    }

    autoReload(){
        setTimeout(
            function() {
                this.fetchData()
            }
            .bind(this),
            3000
        );
    }

    componentDidMount() {
        this.fetchData()
    }

    fetchData(){
        this.reset()
        fetch('https://api.themoviedb.org/3/search/movie?api_key=17c21a1abd8ae7be6ee8986389011906&query='+ this.state.query)
        .then( response => {
            if (!response.ok) { throw response }
            return response.json() 
        })
        .then( json => {
            this.setState({loaded:true, error:false, movie: json.results}) 
        })
        .catch( err => {
            if(err.status == '429'){
                this.setState({error:true})
                this.autoReload()
            }
        })
    }

    handleInputChange = () => {
        this.setState({ loaded: false, query: this.search.value }, () => {
            if (this.state.query && this.state.query.length >= 0) {
                if (this.state.query.length % 2 === 0) {
                    this.fetchData()
                }
            }
        })
    }

    onKeyDown = () => {
        this.setState({ query: this.search.value }, () => {
            if (this.state.query && this.state.query.length >= 1) {
                if (this.state.query.length % 2 === 0) {
                    this.fetchData()
                }
            }
        })
    }

    reload = function() {
        if(!window.location.hash) {
            window.location.reload();
        }
    }

    render() {
        var error = ''
        var map = []
        if(this.state.movie){
            map = Array.from(Array(this.state.movie.length).keys())
        }
        if(this.state.error){
            error = <div className="error"><h1>API Request Limit Reached: Reloading</h1></div>
        }
        return(
            <div>
                <Link to= {{pathname: `/`}}><img className="logo-img-full" src={logo} alt="kino logo"/></Link>
                <div className="header-home">
                        <Container>
                            <Row>
                                <Col xs={12}>
                                    <p className="title">Showing results for: {this.state.query}</p>    
                                </Col>
                                <Col xs={12}>
                                    <div className="search">
                                        <form>
                                            <input
                                                className="search-box"
                                                placeholder="Search movies..."
                                                ref={input => this.search = input}
                                                onChange={this.handleInputChange}
                                                onKeyPress={e => { if (e.key === 'Enter') e.preventDefault(); }}
                                            />
                                        </form>
                                    </div>
                                </Col>
                            </Row>
                        </Container>
                    </div>
                    <div className="body">
                        {error}
                        <CardColumns>
                                {map.map(i=>{
                                    return  <Card>
                                                <FadeIn>
                                                    <Movie id={this.state.movie[i].id}></Movie>
                                                </FadeIn>
                                            </Card>
                                })}
                        </CardColumns> 
                    </div>
                <Footer/> 
            </div>
        )
    }
}

export default Home
