import React, { Component } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import MovieCard from '../components/MovieCard'
import { FaSearch } from 'react-icons/fa'
import '../css/main.css'
import kinoLogo from '../img/logo.png'

class Home extends Component {

    constructor(props){
        super(props);
        this.state = {
            data: [],
            query: 'john',
            loaded: false,
            error: false,
            input: ''
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    componentDidMount() {
        this.fetchData()
    }

    componentDidUpdate(prevState, snapshot){
        if(this.state.query !== prevState.query){
            this.fetchData(this.state.input)
        }
    }

    handleChange(e){
        this.setState({ input: e.target.value})
    }

    handleSubmit(e){
        e.preventDefault()
        this.setState({ data: [], query: this.state.input })
    }

    fetchData(query){
        query = this.state.query
        //Api call based on user query
        fetch('https://api.themoviedb.org/3/search/movie?api_key=17c21a1abd8ae7be6ee8986389011906&query='+ query)
        .then( response => {
            if (!response.ok) { throw response }
            return response.json() 
        })
        //Store data in state
        .then( json => {
            this.setState({loaded:true, error:false, data: json.results}) 
        })
        //Catches API limit errors, reloads page until limit resets
        .catch( err => {
            if(err.status === '429'){
                this.setState({error:true})
                this.fetchData()
            }
        })
    }

    renderHeader(){
        return(
            <Row>
                <Col>
                    <div id="header">
                        <img src={kinoLogo} alt="kino"></img>
                        <h1>Showing results for '{this.state.query}'</h1>
                        <form onSubmit={this.handleSubmit}>
                            <input 
                                type="text"
                                placeholder="Search movies"
                                onChange={this.handleChange}
                            />
                            <button 
                                type="submit" 
                                id="search"
                            >
                                <FaSearch/>
                            </button>
                        </form>
                    </div>
                </Col>
            </Row>
        )
    }

    renderMovies(){
        var data = this.state.data
        var loaded = this.state.loaded
        //If API call was succesfull and component has data, return grid of movies
        if(loaded){
            return (
                <Row className="movie-feed-row">
                    {data.map((item, i) => (
                            <Col sm={3}>
                                <MovieCard id={data[i].id}/>
                            </Col>
                    ))}
                </Row>
            )
        }
        //TODO: Return placeholder for movie loading 
        else{
            return <p>Loading...</p>
        }
    }

    render() {
        return (
            <div id="app">
                <Container>
                    {this.renderHeader()}
                    {this.renderMovies()}
                </Container>
            </div>
        );
    }
}

export default Home;
