import React, { Component } from 'react';
import { Palette } from 'react-palette'
import '../css/movie-card.css'

class MovieCard extends Component {

    state = {
        data: [],
        loaded: false
    }

    componentDidMount(){
        this.fetchData()
    }

    fetchData(){
        fetch('https://api.themoviedb.org/3/movie/' + this.props.id + '?api_key=17c21a1abd8ae7be6ee8986389011906')
        .then(response => {
            if (!response.ok) {
                throw Error("Network request failed")
            }
            return response
        })
        .then(d => d.json())
        .then(d => {
            this.setState({
                data: d,
                loaded: true
            })
        })
    }

    renderPoster(){
        var loaded = this.state.loaded
        if(loaded){
            var poster = this.state.data.poster_path
            if(poster){
                return <img 
                            src={"https://image.tmdb.org/t/p/w500" + poster} 
                            alt="poster" 
                            id="poster"
                        />
            }else{
                return <p>No Image, Sorry.</p>
            }
        }else{
            return <p>No Image, Sorry.</p>
        }
    }

    renderLine(){
        var path = this.state.data.poster_path
        return(
            <Palette src={"https://image.tmdb.org/t/p/w500" + path}>
                {({ data, loading, error }) => (
                    <div style={{ backgroundColor: data.vibrant }}id="line"></div>
                )}
            </Palette>
        )
    }

    renderInfo(){
        var loaded = this.state.loaded
        var data = this.state.data

        if(loaded){
            return(
                <div id="info-container">
                    <div id="line-container">
                        {/* <div id="line"></div> */}
                        {this.renderLine()}
                    </div>
                    <div id="title-container">
                        <p id="title">
                            {data.title}
                            <span id="year"> {data.release_date.substring(0, 4)}</span>
                            <span id="rating">{data.vote_average}</span>
                        </p>
                    </div>
                </div>
            )
        }
    }

    render() {
        return (
            <div id="movie-card">
                <div>
                    {this.renderPoster()}
                    {this.renderInfo()}
                </div>
            </div>
        );
    }
}

export default MovieCard;
