import React, { Component } from 'react'
import MovieCard from '../components/MovieCard'

class Home extends Component {
    state = { 
        data: [],
        query: 'star wars',
        loaded: false,
        error: false
    }

    componentDidMount() {
        this.fetchData()
    }

    fetchData(){
        //Api call based on user query
        fetch('https://api.themoviedb.org/3/search/movie?api_key=17c21a1abd8ae7be6ee8986389011906&query='+ this.state.query)
        .then( response => {
            if (!response.ok) { throw response }
            return response.json() 
        })
        //Store data in state
        .then( json => {
            this.setState({loaded:true, error:false, data: json.results}) 
        })
        //Catches API limit errors, reloads page until limit resets
        .catch( err => {
            if(err.status === '429'){
                this.setState({error:true})
                this.fetchData()
            }
        })
    }

    renderMovies(){
        var data = this.state.data
        var loaded = this.state.loaded
        var error = this.state.error

        if(data && loaded &&!error){
            data.map(i=> {
                return(
                    <MovieCard name={data[0].title}/>
                )
            })
        }else{
            return <p>Loading...</p>
        }
    }

    render() {
        return (
            <div>
                {this.renderMovies()}
            </div>
        );
    }
}

export default Home;
