                                                    <p className="movie-full-overview">{this.state.movieData.overview}</p>
                                                    <div className="stars-container">
                                                        <ReactStars
                                                            count={5}
                                                            value={rating}
                                                            size={24}
                                                            color1={palette.darkMuted}
                                                            color2={palette.vibrant}
                                                            className={'stars-full'}
                                                        />
