import React, { Component } from 'react';
import Modal from 'react-modal'
import { IoIosStar, IoMdTime, IoIosPlay } from 'react-icons/io'
import { Container, Row, Col } from 'react-bootstrap'
import '../css/modal.css'

Modal.setAppElement('#root')

class MovieModal extends Component {

    constructor(props) {
        super(props)
        this.closeModal = this.closeModal.bind(this);
    }

    closeModal(){
        return this.props.isOpen
    }

    getGenres(){
        var genres = this.props.data.genres
        if(genres){
            var string = ''
            for (let i = 0; i < genres.length; i++) {
                string += genres[i].name + ' / '
            }
            return string.substring(0, string.length - 2)
        }else{
            return 'Unknown'
        }
    }

    render() {
        return (
            <div>
                <Modal
                    isOpen={this.props.isOpen}
                    onRequestClose={this.closeModal}
                    id="modal"
                >
                    <button id="button" onClick={this.closeModal}>X</button>

                    <img 
                        src={"https://image.tmdb.org/t/p/original/" + this.props.data.backdrop_path} 
                        id="bgImage"
                        alt='bg'
                    />

                    <Container id="modalContainer">
                        <Row id="modalRow">

                            <Col sm={6} id="modalInfo">
                                <h1>{this.props.data.title}</h1>
                                <div>
                                    <p>{this.props.data.overview}</p>
                                    <button>
                                        <IoIosPlay className="icon"/>
                                        TRAILER
                                    </button>
                                </div>
                            </Col>

                            <Col sm={6} id="modalStats">
                                <p><span>Genres</span>{this.getGenres()}</p>
                                <p><span>Rating</span>{this.props.data.vote_average}</p>
                                <p><span>Release</span>{this.props.data.release_date}</p>
                                <p><span>Revenue</span>{this.props.data.revenue}</p>
                            </Col>
                        </Row>
                    </Container>

                </Modal>
            </div>
        );
    }

  }

export default MovieModal
